insert into gestures (name, description, minor_major, canupgradetomajor, penaltytime, isstickpenalty, image_path) values
('Cambio ilegal','Más de 4 en pista o cambio antes de 3 m del banquillo. Si es deliberado en los 2 últimos minutos, penalti.','minor',true,2,false,'6_en_pista.png'),
