-- Seed: Populate gestures table with data from table.csv, including image_path and boolean canupgradetomajor

on conflict (name) do update set
  description = excluded.description,
  minor_major = excluded.minor_major,
  canupgradetomajor = excluded.canupgradetomajor,
  penaltytime = excluded.penaltytime,
  isstickpenalty = excluded.isstickpenalty,
  image_path = excluded.image_path;